import streamlit as st
import requests
from bs4 import BeautifulSoup
import pandas as pd
from io import BytesIO

# Função para garantir que a URL seja completa
def complete_url(link, base_url="https://casadosdados.com.br"):
    if link.startswith('http'):
        return link
    return base_url + link

# Função para extrair links das empresas
def get_company_links(search_term, uf):
    url = f"https://casadosdados.com.br/solucao/cnpj?q={search_term}&uf={uf}"
    headers = {'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/124.0.0.0 Safari/537.36'}

    response = requests.get(url, headers=headers)
    soup = BeautifulSoup(response.content, 'html.parser')
    
    # Use um seletor CSS ou XPath adaptado para BeautifulSoup
    links = [a['href'] for a in soup.select('a[href]')]  # Adapte este seletor ao seu caso específico

    return links

# Função para extrair dados de uma empresa
def get_company_data(company_url):
    full_url = complete_url(company_url)  # Assegura que a URL é completa
    headers = {
        "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/85.0.4183.121 Safari/537.36"
    }
    response = requests.get(full_url, headers=headers)
    soup = BeautifulSoup(response.content, 'html.parser')
    
    # Extrai todos os divs que contêm as informações
    data_divs = soup.select('div[data-v-330d3ab8]')
    data = {}
    
    # Processa cada div para extrair as informações de label e conteúdo
    for div in data_divs:
        label = div.find('label')
        content = div.find('p', class_='has-text-weight-bold')
        
        if label and content:  # Garante que ambos label e content existam
            key = label.text.strip().replace(':', '')  # Remove dois pontos se houver no final de label
            value = content.text.strip()
            data[key] = value

    return data

def to_excel(df):
    output = BytesIO()
    with pd.ExcelWriter(output, engine='openpyxl') as writer:
        df.to_excel(writer, index=False, sheet_name='Sheet1')
    processed_data = output.getvalue()
    return processed_data


